                            sw = new google.maps.LatLng(viewportObject.sw.lat, viewportObject.sw.lng);
                            viewport = new google.maps.LatLngBounds(sw, ne);
                        } else {
                            refreshMapJSONFunc($(autoCompleteElt).val());
