                    url = url.replace('fr', lang);
                    url += "?token=de49lrl0CsaP8wfsISKdNE9zD4-RzuZv1ZxM6DVk6Y4";
                    $.getJSON(
                        url,
                        function (queryResult) {

                            if (queryResult.status == 'OK' && queryResult.results.length) {
                                var resultsT = queryResult.results;
